import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";
import Axios from "axios";
import { Link, Navigate, useNavigate } from "react-router-dom";

function process_query()
{
  async () => {
    await fetch("http://localhost:8080/query", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        withCredentials: true,
        crossorigin: true,
        mode: "no-cors",
      },
      body: query,
    });
    setTimeout(() => {
      navigate("/resultsPage");
    }, 1000);
  }
}

function SearchPage() {
  const [query, setQuery] = useState();
  const [theme, setTheme] = useState("light");
  const navigate = useNavigate();

  return (


    

    <div className = { "cover " + theme}>

    <div class="container-fluid centered">
      <div class="row">
        
      { 
        theme == "light" ?
        
        <h1 class="SEName-light col-12">
          <span class="GBlue">D</span>
          <span class="GRed">o</span>
          <span class="GYellow">o</span>
          <span class="GBlue">d</span>
          <span class="GGreen">l</span>
          <span class="GRed">e</span>
        </h1>
        :
        <h1 class="SEName-dark col-12">
          D
          o
          o
          d
          l
          e        
        </h1>
      }
        <div class="col-12">
          <div class="input-group mb-3 mt-5 mb-5" style = {{width : 60 + '%', margin : "auto"}}>
            <input
              type="text"
              class={theme == "dark" ? "form-control searchField-dark": "form-control"}
              id="searchField"
              placeholder="Search Doodle or type a URL"
              onChange = {(e) => {setQuery(e.target.value)}}
            />
          </div>
        </div>

        <div class="col-12 mt-1">

          <div class="container">
            <div class="row justify-content-center">
              <div class="col-2"><button id="find" class={"btn" + (theme == "dark" ? " btn-dark" :"")} onClick={console.log(query)}>Doodle Search</button></div>
              <div class="col-2"><button class={(theme == "dark" ? "btn btn-dark" :"btn")}>i'm feeling lucky</button></div>
            </div>
          </div>

        </div>
      </div>
    </div>
    </div>

/*
    <div>
      <input
        type="text"
        onChange={(e) => {
          setQuery(e.target.value);
        }}
      ></input>
      <button
        onClick={process_query()}
      >
        search
      </button>
    </div>
*/
  );
}

export default SearchPage;
