import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";
import Axios from "axios";
import { Link, Navigate, useNavigate } from "react-router-dom";

function process_query()
{
  async () => {
    await fetch("http://localhost:8080/query", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        withCredentials: true,
        crossorigin: true,
        mode: "no-cors",
      },
      body: query,
    });
    setTimeout(() => {
      navigate("/resultsPage");
    }, 1000);
  }
}

function SearchPage() {
  const [query, setQuery] = useState();
  const [theme, setTheme] = useState("dark");
  const navigate = useNavigate();

  let doodle = null;

  return (


    <div className = { "cover " + theme}>

    <div class="container-fluid centered">
      <div class="row">
        <h1 class="SEName col-12">


          if (theme == "light") {
            doodle = "<span class='GRed'>o</span>"
            +"<span class='GYellow'>o</span>"
            +"<span class='GBlue'>d</span>"
            +"<span class='GGreen'>l</span>"
            +"<span class='GRed'>e</span>"
          }
          else
          {
            doodle = ""
          }

          <span class="GBlue">D</span>




        </h1>

        <div class="col-12">
          <div class="input-group mb-3 mt-5 mb-5" style = {{width : 60 + '%', margin : "auto"}}>
            <input
              type="text"
              class="form-control"
              id="searchField"
              placeholder="Search Doodle or type a URL"
              onChange = {(e) => {setQuery(e.target.value)}}
            />
          </div>
        </div>

        <div class="col-12 mt-1">

          <div class="container">
            <div class="row justify-content-center">
              <div class="col-2"><button id="find" class="btn" onClick={console.log(query)}>Doodle Search</button></div>
              <div class="col-2"><button class="btn">i'm feeling lucky</button></div>
            </div>
          </div>

        </div>
      </div>
    </div>
    </div>

/*
    <div>
      <input
        type="text"
        onChange={(e) => {
          setQuery(e.target.value);
        }}
      ></input>
      <button
        onClick={process_query()}
      >
        search
      </button>
    </div>
*/
  );
}

export default SearchPage;
